import React from "react";
import "./homeStyle.scss";
import { Link } from "react-router-dom";
import SplitText from "react-pose-text";
import Button from "@material-ui/core/Button";
import posed, { PoseGroup } from "react-pose";
import { tween } from "popmotion";
import { interpolate } from "flubber";

const charPoses = {
  exit: { opacity: 0, y: 20 },
  enter: {
    opacity: 1,
    y: 0,
    delay: ({ charIndex }) => charIndex * 30
  }
};

//svgs
const paths = {
  plane:
    "M510,255c0-20.4-17.85-38.25-38.25-38.25H331.5L204,12.75h-51l63.75,204H76.5l-38.25-51H0L25.5,255L0,344.25h38.25 l38.25-51h140.25l-63.75,204h51l127.5-204h140.25C492.15,293.25,510,275.4,510,255z",
  circle:
    "M255,0C114.75,0,0,114.75,0,255s114.75,255,255,255s255-114.75,255-255S395.25,0,255,0z",
  heart:
    "M 0 100 H 90 V 90 H 10 L 10 10",
  bookmark:
      "m 70, 100 c -1.8937,-3.6423 -7.2107,-12.2663 -11.8158,-19.1644 l -8.3728,-12.542 1.8905,-4.6826 c 2.7274,-6.7555 2.6201,-14.8806 -0.2478,-18.7558 -3.8529,-5.2063 -7.9362,-6.5964 -19.3764,-6.5964 -9.6851,0 -10.7337,-0.2386 -16.0763,-3.6585 -3.1435,-2.0122 -7.1701,-4.2083 -8.9481,-4.8803 -4.7192,-1.7837 -12.5196,-1.493 -15.4743,0.5766 -3.4155,2.3923 -6.7286,9.095 -7.7011,15.58 -0.7643,5.0969 -1.0165,5.3744 -7.8003,8.5853 -3.8557,1.825 -12.684,6.5423 -19.6184,10.483 -6.9344,3.9406 -12.7782,6.9946 -12.9863,6.7865 -0.899,-0.8991 14.8089,-20.5933 24.3019,-30.4691 12.8015,-13.3178 22.5481,-20.8291 34.473,-26.5666 12.1922,-5.8662 19.3136,-7.0972 27.6439,-4.7785 15.2518,4.2451 29.7787,25.5445 37.7581,55.3608 3.3763,12.6161 6.6712,32.818 6.1609,37.7733 -0.3592,3.4878 -0.448,3.4168 -3.8107,-3.0513 z"
};

const pathIds = Object.keys(paths);

const morphTransition = ({ from, to }) =>
  tween({
    from: 0,
    to: 1
  }).pipe(
    interpolate(from, to));

const Icon = posed.path(
  pathIds.reduce((config, id) => {
    config[id] = {
      d: paths[id],
      transition: morphTransition,
    };
    return config;
  }, {})
);

export default class HomePage extends React.Component {
  state = { pathIndex: 0 };

  gotoNext = () => {
    const { pathIndex } = this.state;
    const nextIndex = pathIndex + 1;
    this.setState({
      pathIndex: nextIndex > pathIds.length - 1 ? 0 : nextIndex
    });
  };

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  componentDidMount() {
    this.interval = setInterval(this.gotoNext, 2000);
  }

  render() {
    return (
      <div className="home-container page">
        <svg width="400" height="400" viewBox="0 0 520 500">
          <Icon pose={pathIds[this.state.pathIndex]} />
        </svg>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          Welcome aboard!
        </SplitText>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          My name is Aarushi.
        </SplitText>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          Glad to have you here!
        </SplitText>
        <br></br>
        <Link to="/hobbies">
          <Button color="primary" variant="contained">
            Hobbies
          </Button>
        </Link>
        <Link to="/professional">
          <Button color="primary" variant="contained">
            Professional
          </Button>
        </Link>
      </div>
    );
  }
}
