import React from "react";
import "./homeStyle.scss";
import { Link } from "react-router-dom";
import SplitText from "react-pose-text";
import Button from "@material-ui/core/Button";
import posed, { PoseGroup } from "react-pose";
import { tween } from "popmotion";
import { interpolate } from "flubber";

const charPoses = {
  exit: { opacity: 0, y: 20 },
  enter: {
    opacity: 1,
    y: 0,
    delay: ({ charIndex }) => charIndex * 30
  }
};

const styles = [
  {
    opacity: 1,
    fill: "#ffffff",
    fillOpacity: 1,
    fillRule: "evenodd",
    stroke: "#000000",
    strokeWidth: 0,
    strokeLinejoin: "round",
    strokeMiterlimit: 4,
    strokeDasharray: "none",
    strokeDashoffset: 0,
    strokeOpacity: 1
  },
  {
    opacity:1,
    fill: '#cccccc',
    fillOpacity:1,
    fillRule: 'evenodd',
    stroke: '#000000',
    strokeWidth:0.05741542,
    strokeLinejoin: 'round',
    strokeMiterlimit:4,
    strokeDasharray: 'none',
    strokeDashoffset: 0,
    strokeOpacity:1
  },
  {
    opacity: 1,
    fill: "#ffffff",
    fillOpacity: 1,
    fillRule: "evenodd",
    stroke: "#000000",
    strokeWidth: 0,
    strokeLinejoin: "round",
    strokeMiterlimit: 4,
    strokeDasharray: "none",
    strokeDashoffset: 0,
    strokeOpacity: 1
  },
  {
    opacity: 1,
    fill: "#ffffff",
    fillOpacity: 1,
    fillRule: "evenodd",
    stroke: "#000000",
    strokeWidth: 0,
    strokeLinejoin: "round",
    strokeMiterlimit: 4,
    strokeDasharray: "none",
    strokeDashoffset: 0,
    strokeOpacity: 1
  }
];

//svgs
const paths = {
  plane:
    "m 250.42882,300.396741 c 96.457909,-303.153429 -397.34272,-47.739309 -94.18929,48.718601 303.153423,96.457908 47.7393,-397.34272 -48.7186,-94.18929 -96.45791,303.15342 397.34272,47.739303 94.18929,-48.71861 -303.15343,-96.4579 -47.73931,397.34273 48.7186,94.189299 z",
  circle:
    "m 250.01811,300.16886 -248.35846,-40.3395 -248.35847,-40.33951 159.11427,-194.91503 159.11428,-194.915 89.24419,235.2545 z",
  heart: "M 0 100 H 90 V 90 H 10 L 10 10",
  bookmark: "M 0 100 H 90 V 90 H 10 L 10 10"
};

const pathIds = Object.keys(paths);

const morphTransition = ({ from, to }) =>
  tween({
    from: 0,
    to: 1
  }).pipe(interpolate(from, to));

const Icon = posed.path(
  pathIds.reduce((config, id) => {
    config[id] = {
      d: paths[id],
      transition: morphTransition
    };
    return config;
  }, {})
);

export default class HomePage extends React.Component {
  state = { pathIndex: 0 };

  gotoNext = () => {
    const { pathIndex } = this.state;
    const nextIndex = pathIndex + 1;
    this.setState({
      pathIndex: nextIndex > pathIds.length - 1 ? 0 : nextIndex
    });
  };

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  componentDidMount() {
    this.interval = setInterval(this.gotoNext, 2000);
  }

  render() {
    return (
      <div className="home-container page">
        <svg width="400" height="400" viewBox="0 0 520 500">
          <Icon style={styles[this.state.pathIndex]} pose={pathIds[this.state.pathIndex]} />
        </svg>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          Welcome aboard!
        </SplitText>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          My name is Aarushi.
        </SplitText>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          Glad to have you here!
        </SplitText>
        <br></br>
        <Link to="/hobbies">
          <Button color="primary" variant="contained">
            Hobbies
          </Button>
        </Link>
        <Link to="/professional">
          <Button color="primary" variant="contained">
            Professional
          </Button>
        </Link>
      </div>
    );
  }
}
