import React from "react";
import "./homeStyle.scss";
import { Link } from "react-router-dom";
import SplitText from "react-pose-text";
import Button from "@material-ui/core/Button";
import posed, { PoseGroup } from "react-pose";
import { tween } from "popmotion";
import { interpolate } from "flubber";

const charPoses = {
  exit: { opacity: 0, y: 20 },
  enter: {
    opacity: 1,
    y: 0,
    delay: ({ charIndex }) => charIndex * 30
  }
};

//svgs
const paths = {
  plane:
    "M510,255c0-20.4-17.85-38.25-38.25-38.25H331.5L204,12.75h-51l63.75,204H76.5l-38.25-51H0L25.5,255L0,344.25h38.25 l38.25-51h140.25l-63.75,204h51l127.5-204h140.25C492.15,293.25,510,275.4,510,255z",
  circle:
    "M255,0C114.75,0,0,114.75,0,255s114.75,255,255,255s255-114.75,255-255S395.25,0,255,0z",
  heart:
    "M 20 100 H 90 V 90 H 10 L 10 10",
  bookmark:
      "M 10 10 H 90 V 90 H 10 L 10 10"
};

const pathIds = Object.keys(paths);

const morphTransition = ({ from, to }) =>
  tween({
    from: 0,
    to: 1
  }).pipe(
    interpolate(from, to));

const Icon = posed.path(
  pathIds.reduce((config, id) => {
    config[id] = {
      d: paths[id],
      transition: morphTransition,
    };
    return config;
  }, {})
);

export default class HomePage extends React.Component {
  state = { pathIndex: 0 };

  gotoNext = () => {
    const { pathIndex } = this.state;
    const nextIndex = pathIndex + 1;
    this.setState({
      pathIndex: nextIndex > pathIds.length - 1 ? 0 : nextIndex
    });
  };

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  componentDidMount() {
    this.interval = setInterval(this.gotoNext, 2000);
  }

  render() {
    return (
      <div className="home-container page">
        <svg width="400" height="400" viewBox="0  500">
          <Icon pose={pathIds[this.state.pathIndex]} />
        </svg>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          Welcome aboard!
        </SplitText>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          My name is Aarushi.
        </SplitText>
        <br></br>
        <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
          Glad to have you here!
        </SplitText>
        <br></br>
        <Link to="/hobbies">
          <Button color="primary" variant="contained">
            Hobbies
          </Button>
        </Link>
        <Link to="/professional">
          <Button color="primary" variant="contained">
            Professional
          </Button>
        </Link>
      </div>
    );
  }
}
